'use strict';

var randu = require( '@stdlib/random/base/randu' );
var pdf = require( './../lib' );

var beta;
var mu;
var x;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	x = randu() * 10.0;
	mu = randu() * 10.0;
	beta = randu() * 10.0;
	y = pdf( x, mu, beta );
	console.log( 'x: %d, µ: %d, β: %d, f(x;µ,β): %d', x.toFixed( 4 ), mu.toFixed( 4 ), beta.toFixed( 4 ), y.toFixed( 4 ) );
}
