'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var beta;
var mu;
var p;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	p = randu();
	mu = randu() * 10.0;
	beta = randu() * 10.0;
	y = quantile( p, mu, beta );
	console.log( 'p: %d, µ: %d, β: %d, Q(p;µ,β): %d', p.toFixed( 4 ), mu.toFixed( 4 ), beta.toFixed( 4 ), y.toFixed( 4 ) );
}
