'use strict';

var randu = require( '@stdlib/random/base/randu' );
var pdf = require( './../lib' );

var alpha;
var beta;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 2.0;
	alpha = randu() * 5.0;
	beta = randu() * 5.0;
	y = pdf( x, alpha, beta );
	console.log( 'x: %d, α: %d, β: %d, f(x;α,β): %d', x.toFixed( 4 ), alpha.toFixed( 4 ), beta.toFixed( 4 ), y.toFixed( 4 ) );
}
