'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var alpha;
var beta;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	alpha = randu() * 5.0;
	beta = randu() * 5.0;
	y = quantile( p, alpha, beta );
	console.log( 'p: %d, α: %d, β: %d, Q(p;α,β): %d', p.toFixed( 4 ), alpha.toFixed( 4 ), beta.toFixed( 4 ), y.toFixed( 4 ) );
}
