'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var logpdf = require( './../lib' );

var a;
var b;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu();
	a = ( randu()*5.0 ) + EPS;
	b = ( randu()*5.0 ) + EPS;
	y = logpdf( x, a, b );
	console.log( 'x: %d, a: %d, b: %d, ln(f(x;a,b)): %d', x.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), y.toFixed( 4 ) );
}
