'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var stdev = require( './../lib' );

var a;
var b;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	a = randu() * 10.0;
	b = randu() * 10.0;
	v = stdev( a, b );
	console.log( 'a: %d, b: %d, SD(X;a,b): %d', a.toFixed( 4 ), b.toFixed( 4 ), v.toFixed( 4 ) );
}

