'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var mu;
var b;
var p;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	p = randu();
	mu = randu() * 10.0;
	b = randu() * 10.0;
	y = quantile( p, mu, b );
	console.log( 'p: %d, Âµ: %d, b: %d, Q(p;Âµ,b): %d',
		p.toFixed( 4 ),
		mu.toFixed( 4 ),
		b.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
