'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var logcdf = require( './../lib' );

var mu;
var c;
var x;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	mu = randu() * 10.0;
	x = ( randu()*10.0 ) + mu;
	c = ( randu()*10.0 ) + EPS;
	y = logcdf( x, mu, c );
	console.log( 'x: %d, Âµ: %d, c: %d, ln(F(x;Âµ,c)): %d', x.toFixed( 4 ), mu.toFixed( 4 ), c.toFixed( 4 ), y.toFixed( 4 ) );
}
