'use strict';

var randu = require( '@stdlib/random/base/randu' );
var cdf = require( './../lib' );

var i;
var r;
var p;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 50;
	r = randu() * 50;
	p = randu();
	y = cdf( x, r, p );
	console.log( 'x: %d, r: %d, p: %d, F(x;r,p): %d', x.toFixed( 4 ), r.toFixed( 4 ), p.toFixed( 4 ), y.toFixed( 4 ) );
}
