'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pmf = require( './../lib' );

var i;
var r;
var p;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	x = round( randu() * 30 );
	r = randu() * 50;
	p = randu();
	y = pmf( x, r, p );
	console.log( 'x: %d, r: %d, p: %d, P(X=x;r,p): %d', x, r, p.toFixed( 4 ), y.toFixed( 4 ) );
}
