'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var i;
var k;
var r;
var p;
var y;

for ( i = 0; i < 10; i++ ) {
	k = randu();
	r = randu() * 100;
	p = randu();
	y = quantile( k, r, p );
	console.log( 'k: %d, r: %d, p: %d, Q(k;r,p): %d', k.toFixed( 4 ), r.toFixed( 4 ), p.toFixed( 4 ), y );
}
