'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var variance = require( './../lib' );

var v;
var i;
var r;
var p;

for ( i = 0; i < 10; i++ ) {
	r = randu() * 100;
	p = randu();
	v = variance( r, p );
	console.log( 'r: %d, p: %d, Var(X;r,p): %d',
		r,
		p.toFixed( 4 ),
		v.toFixed( 4 )
	);
}

