'use strict';

var randu = require( '@stdlib/random/base/randu' );
var logpdf = require( './../lib' );

var sigma;
var mu;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = randu() * 10.0;
	mu = (randu() * 10.0) - 5.0;
	sigma = randu() * 20.0;
	y = logpdf( x, mu, sigma );
	console.log( 'x: %d, µ: %d, σ: %d, ln(f(x;µ,σ)): %d', x.toFixed( 4 ), mu.toFixed( 4 ), sigma.toFixed( 4 ), y.toFixed( 4 ) );
}
