'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var sigma;
var mu;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	mu = (randu() * 10.0) - 5.0;
	sigma = randu() * 20.0;
	y = quantile( p, mu, sigma );
	console.log( 'p: %d, µ: %d, σ: %d, Q(p;µ,σ): %d', p, mu, sigma, y );
}
