'use strict';

// MODULES //

var PINF = require( '@stdlib/math/constants/float64-pinf' );


// MAIN //

/**
* Returns the expected value of a Pareto (Type I) distribution.
*
* @param {PositiveNumber} alpha - shape parameter
* @param {PositiveNumber} beta - scale parameter
* @returns {PositiveNumber} expected value
*
* @example
* var v = mean( 1.0, 1.0 );
* // returns Infinity
* @example
* var v = mean( 4.0, 12.0 );
* // returns 16.0
* @example
* var v = mean( 8.0, 2.0 );
* // returns ~2.286
* @example
* var v = mean( 1.0, -0.1 );
* // returns NaN
* @example
* var v = mean( -0.1, 1.0 );
* // returns NaN
* @example
* var v = mean( 2.0, NaN );
* // returns NaN
* @example
* var v = mean( NaN, 2.0 );
* // returns NaN
*/
function mean( alpha, beta ) {
	if ( alpha <= 0.0 || beta <= 0.0 ) {
		return NaN;
	}
	if ( alpha <= 1.0 ) {
		return PINF;
	}
	return ( alpha*beta ) / ( alpha-1.0 );
} // end FUNCTION mean()


// EXPORTS //

module.exports = mean;
