'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pmf = require( './../lib' );

var lambda;
var x;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	x = round( randu() * 10.0 );
	lambda = randu() * 10.0;
	y = pmf( x, lambda );
	console.log( 'x: %d, Î»: %d, P(X=x;Î»): %d', x, lambda.toFixed( 4 ), y.toFixed( 4 ) );
}
