'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var lambda;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	lambda = randu() * 10.0;
	y = quantile( p, lambda );
	console.log( 'p: %d, Î»: %d, Q(p;Î»): %d', p.toFixed( 4 ), lambda.toFixed( 4 ), y );
}
