'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var sigma;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	sigma = randu() * 10.0;
	y = quantile( p, sigma );
	console.log( 'p: %d, Ïƒ: %d, Q(p;Ïƒ): %d', p.toFixed( 4 ), sigma.toFixed( 4 ), y.toFixed( 4 ) );
}
