'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Returns the mode of a Student's t distribution.
*
* @param {NonNegativeNumber} v - degrees of freedom
* @returns {NonNegativeNumber} mode
*
* @example
* var v = mode( 9.0 );
* // returns 0.0
* @example
* var v = mode( 2.0 );
* // returns 0.0
* @example
* var v = mode( -0.2 );
* // returns NaN
* @example
* var v = mode( NaN );
* // returns NaN
*/
function mode( v ) {
	if ( isnan( v ) || v < 0.0 ) {
		return NaN;
	}
	return 0.0;
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
