'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var v;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	v = randu() * 10.0;
	y = quantile( p, v );
	console.log( 'p: %d, v: %d, Q(p;v): %d', p.toFixed( 4 ), v.toFixed( 4 ), y.toFixed( 4 ) );
}
