'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var stdev = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof stdev, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for `v`, the function returns `NaN`', function test( t ) {
	var v = stdev( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'if provided a degrees of freedom `v` that is not a nonnegative number, the function returns `NaN`', function test( t ) {
	var v;

	v = stdev( -1.0 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = stdev( NINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided `1 < v <= 2`, the function returns `infinity`', function test( t ) {
	var v = stdev( 1.5 );
	t.equal( v, PINF, 'returns infinity' );

	v = stdev( 1.1 );
	t.equal( v, PINF, 'returns infinity' );

	v = stdev( 2.0 );
	t.equal( v, PINF, 'returns infinity' );

	t.end();
});

tape( 'the function returns the standard deviation of a Student\'s t distribution', function test( t ) {
	var expected;
	var v;
	var i;
	var y;

	expected = data.expected;
	v = data.v;
	for ( i = 0; i < expected.length; i++ ) {
		y = stdev( v[i] );
		t.equal( y, expected[i], 'v: '+v[i]+', y: '+y+', expected: '+expected[i] );
	}
	t.end();
});
