'use strict';

var randu = require( '@stdlib/random/base/randu' );
var logpdf = require( './../lib' );

var a;
var b;
var c;
var x;
var y;
var i;

for ( i = 0; i < 25; i++ ) {
	x = randu() * 30.0;
	a = randu() * 10.0;
	b = a + (randu() * 40.0);
	c = a + ((b-a) * randu());
	y = logpdf( x, a, b, c );
	console.log( 'x: %d, a: %d, b: %d, c: %d, ln(f(x;a,b,c)): %d', x.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), c.toFixed( 4 ), y.toFixed( 4 ) );
}
