'use strict';

var randu = require( '@stdlib/random/base/randu' );
var quantile = require( './../lib' );

var a;
var b;
var c;
var p;
var y;
var i;

for ( i = 0; i < 25; i++ ) {
	p = randu();
	a = randu() * 10.0;
	b = a + (randu() * 40.0);
	c = a + ((b-a) * randu());
	y = quantile( p, a, b, c );
	console.log( 'p: %d, a: %d, b: %d, c: %d, Q(p;a,b,c): %d', p.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), c.toFixed( 4 ), y.toFixed( 4 ) );
}
