'use strict';

var randu = require( '@stdlib/random/base/randu' );
var mgf = require( './../lib' );

var a;
var b;
var t;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu();
	a = randu() * 5.0;
	b = a + (randu() * 5.0);
	v = mgf( t, a, b );
	console.log( 't: %d, a: %d, b: %d, M_X(t;a,b): %d', t.toFixed( 4 ), a.toFixed( 4 ), b.toFixed( 4 ), v.toFixed( 4 ) );
}
