tape( 'when called without arguments, the function returns a PRNG that returns `NaN` if provided a first argument which is not a positive integer', function test( t ) {
	var binomial;
	var r;

	binomial = factory();

	r = binomial( 3.14, 0.5 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	r = binomial( 0.0, 0.5 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	r = binomial( -1.0, 0.5 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	t.end();
});

tape( 'when called without arguments, the function returns a PRNG that returns `NaN` if provided a second argument which is not a probability', function test( t ) {
	var binomial;
	var r;

	binomial = factory();

	r = binomial( 2, 3.14 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	r = binomial( 2, -1.0 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	r = binomial( 2, 1.5 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns a PRNG for generating random numbers from a binomial distribution', opts, function test( t ) {
	var binomial;
	var rejected;
	var repeats;
	var pValue;
	var probs;
	var freq;
	var bpmf;
	var N;
	var n;
	var p;
	var i;
	var j;
	var x;

	n = 20;
	p = 0.67;
	bpmf = pmf( n, p );

	freq = new Array( n+1 );
	probs = new Array( freq.length );
	for ( j = 0; j < freq.length; j++ ) {
		probs[ j ] = bpmf( j );
	}
	repeats = 100;
	N = 1e3;
	rejected = 0;
	for ( i = 0; i < repeats; i++ ) {
		binomial = factory( n, p );
		t.ok( true, 'seed: '+binomial.SEED );

		// Reset the `freq` array...
		for ( j = 0; j < freq.length; j++ ) {
			freq[ j ] = 0;
		}
		for ( j = 0; j < N; j++ ) {
			x = binomial();
			freq[ x ] += 1;
		}
		// Test using chi-square goodness-of-fit test:
		pValue = chi2gof( freq, probs, {
			'rescale': true,
			'simulate': true,
			'iterations': 500
		}).pValue;
		t.equal( typeof pValue, 'number', 'returns a p-value: '+pValue );
		if ( pValue < 0.05 ) {
			rejected += 1;
		}
	}
	// Account for small sample sizes and few repeats...
	t.ok( rejected / repeats < 0.15, 'null hypothesis (i.e., that numbers are drawn from Binomial('+n+','+p+') is rejected in less than 15% of cases ('+rejected+' of '+repeats+')' );
	t.end();
});

