'use strict';

// MODULES //

var tape = require( 'tape' );
var uniform = require( '@stdlib/math/base/random/uniform' ).factory;
var round = require( '@stdlib/math/base/special/round' );
var isNonNegativeInteger = require( '@stdlib/math/base/assert/is-nonnegative-integer' );
var binomial = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof binomial, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to generate pseudorandom number generators', function test( t ) {
	t.equal( typeof binomial.factory, 'function', 'has `factory` method' );
	t.end();
});

tape( 'attached to the main export is the generator name', function test( t ) {
	t.equal( binomial.NAME, 'binomial', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the main export is the underlying PRNG', function test( t ) {
	t.equal( typeof binomial.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});

tape( 'attached to the main export is the generator seed', function test( t ) {
	t.equal( typeof binomial.SEED, 'number', 'has `SEED` property' );
	t.end();
});

tape( 'the function returns pseudorandom numbers', function test( t ) {
	var rand;
	var n;
	var p;
	var r;
	var i;

	rand = uniform({
		'seed': 3893
	});
	for ( i = 0; i < 1e2; i++ ) {
		n = round( rand( 1.0, 100 ) );
		p = rand( 0.0, 1.0 );
		r = binomial( n, p );
		t.strictEqual( isNonNegativeInteger( r ), true, 'Binomial('+n+','+p+') => '+r );
	}
	t.end();
});
