'use strict';

// MODULES //

var quantile = require( '@stdlib/math/base/dist/cosine/quantile' );


// MAIN //

/**
* Returns pseudorandom number drawn from a raised cosine distribution using inverse transform sampling.
*
* @private
* @param {Function} rand - PRNG for uniformly distributed numbers
* @param {number} mu - mean
* @param {PositiveNumber} s - scale parameter
* @returns {number} pseudorandom number
*/
function sample( rand, mu, s ) {
	return quantile( rand(), mu, s );
} // end FUNCTION sample()


// EXPORTS //

module.exports = sample;
