'use strict';

// MODULES //

var tape = require( 'tape' );
var kstest = require( '@stdlib/math/generics/statistics/kstest' );
var round = require( '@stdlib/math/base/special/round' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var factory = require( './../lib/factory.js' );


// VARIABLES //

var opts = {
	'skip': ( process.env.TEST_MODE === 'coverage' )
};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var gumbel;
	var r;
	var i;

	// When binding distribution parameters...
	gumbel = factory( 0.0, 2.0 );
	for ( i = 0; i < 100; i++ ) {
		r = gumbel();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameters...
	gumbel = factory();
	for ( i = 0; i < 100; i++ ) {
		r = gumbel( 2.5, 2.0 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var gumbel1;
	var gumbel2;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	gumbel1 = factory( 0.0, 1.0, {
		'seed': seed
	});
	gumbel2 = factory( 0.0, 1.0, {
		'seed': seed
	});

	t.notEqual( gumbel1, gumbel2, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = gumbel1();
		r2 = gumbel2();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var gumbel = factory();
	t.equal( gumbel.NAME, 'gumbel', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the underlying PRNG', function test( t ) {
	var gumbel = factory();
	t.equal( typeof gumbel.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var gumbel = factory({
		'seed': 12345
	});
	t.equal( typeof gumbel.SEED, 'number', 'has `SEED` property' );
	t.equal( gumbel.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if provided a shape parameter which is not a positive number', function test( t ) {
	var values;
	var i;

	values = [
		-2.0,
		0.0,
		NaN,
		'5',
		null,
		true,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( 0.0, value );
		};
	}
});

