'use strict';

// MODULES //

var tape = require( 'tape' );
var ENV = require( '@stdlib/utils/env' );
var kstest = require( '@stdlib/math/statistics/kstest' );
var round = require( '@stdlib/math/base/special/round' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var factory = require( './../lib/factory.js' );


// VARIABLES //

var opts = {
	'skip': ( ENV.TEST_MODE === 'coverage' )
};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var invgamma;
	var r;
	var i;

	// When binding distribution parameters...
	invgamma = factory( 2.0, 2.0 );
	for ( i = 0; i < 100; i++ ) {
		r = invgamma();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameters...
	invgamma = factory();
	for ( i = 0; i < 100; i++ ) {
		r = invgamma( 2.5, 2.0 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var invgamma1;
	var invgamma2;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	invgamma1 = factory( 1.0, 1.0, {
		'seed': seed
	});
	invgamma2 = factory( 1.0, 1.0, {
		'seed': seed
	});

	t.notEqual( invgamma1, invgamma2, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = invgamma1();
		r2 = invgamma2();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var invgamma = factory();
	t.equal( invgamma.NAME, 'invgamma', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the underlying PRNG', function test( t ) {
	var invgamma = factory();
	t.equal( typeof invgamma.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var invgamma = factory({
		'seed': 12345
	});
	t.equal( typeof invgamma.SEED, 'number', 'has `SEED` property' );
	t.equal( invgamma.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if `alpha` is not a positive number', function test( t ) {
	var values;
	var i;

	values = [
		-2.0,
		0.0,
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( value, 2.0 );
		};
	}
});

