'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isObject = require( '@stdlib/assert/is-plain-object' );
var randu = require( '@stdlib/math/base/random/randu' ).factory;
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var validate = require( './validate.js' );
var levy0 = require( './_levy.js' );


// MAIN //

/**
* Returns a pseudorandom number generator for generating random numbers from a Lévy distribution.
*
* @param {number} [mu] - mean
* @param {PositiveNumber} [c] - scale parameter
* @param {Options} [options] - function options
* @param {*} [options.seed] - pseudorandom number generator seed
* @throws {TypeError} `mu` must be a number
* @throws {TypeError} `c` must be a positive number
* @throws {TypeError} options argument must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var levy = factory( 0.0, 1.0 );
* var v = levy();
* // returns <number>
*
* @example
* var levy = factory( -3.0, 0.5, {
*     'seed': 297
* });
* var v = levy();
* // returns ~-2.452
*/
function factory() {
	var opts;
	var rand;
	var prng;
	var err;
	var mu;
	var c;

	if ( arguments.length === 0 ) {
		rand = randu();
	} else if ( arguments.length === 1 ) {
		opts = arguments[ 0 ];
		if ( !isObject( opts ) ) {
			throw new TypeError( 'invalid input argument. Options argument must be an object. Value: `' + opts + '`.' );
		}
		rand = randu( opts );
	} else {
		mu = arguments[ 0 ];
		c = arguments[ 1 ];
		err = validate( mu, c );
		if ( err ) {
			throw err;
		}
		if ( arguments.length > 2 ) {
			opts = arguments[ 2 ];
			if ( !isObject( opts ) ) {
				throw new TypeError( 'invalid input argument. Options argument must be an object. Value: `' + opts + '`.' );
			}
			rand = randu( opts );
		} else {
			rand = randu();
		}
	}
	if ( mu === void 0 ) {
		prng = levy2;
	} else {
		prng = levy1;
	}
	setReadOnly( prng, 'NAME', 'levy' );
	setReadOnly( prng, 'SEED', rand.SEED );
	setReadOnly( prng, 'PRNG', rand );

	return prng;

	/**
	* Returns a pseudorandom number drawn from a Lévy distribution with bound parameters.
	*
	* @private
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = levy1();
	* // returns <number>
	*/
	function levy1() {
		return levy0( rand, mu, c );
	} // end FUNCTION levy1()

	/**
	* Returns a pseudorandom number drawn from a Lévy distribution.
	*
	* @private
	* @param {number} mu - mean
	* @param {PositiveNumber} c - scale parameter
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = levy2( 0.0, 2.0 );
	* // returns <number>
	*
	* @example
	* var v = levy2( 0.0, -1.0 );
	* // returns NaN
	*/
	function levy2( mu, c ) {
		if (
			isnan( mu ) ||
			isnan( c ) ||
			c <= 0.0
		) {
			return NaN;
		}
		return levy0( rand, mu, c );
	} // end FUNCTION levy2()
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
