'use strict';

// MODULES //

var tape = require( 'tape' );
var round = require( '@stdlib/math/base/special/round' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var isNonNegativeInteger = require( '@stdlib/math/base/utils/is-nonnegative-integer' );
var factory = require( './../lib/factory.js' );


// VARIABLES //

var opts = {};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var negativeBinomial;
	var r;
	var i;

	// When binding distribution parameters...
	negativeBinomial = factory( 80, 0.05 );
	for ( i = 0; i < 100; i++ ) {
		r = negativeBinomial();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameters...
	negativeBinomial = factory();
	for ( i = 0; i < 100; i++ ) {
		r = negativeBinomial( 120, 0.75 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var negativeBinomial1;
	var negativeBinomial2;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	negativeBinomial1 = factory( 20, 0.3, {
		'seed': seed
	});
	negativeBinomial2 = factory( 20, 0.3, {
		'seed': seed
	});

	t.notEqual( negativeBinomial1, negativeBinomial2, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = negativeBinomial1();
		r2 = negativeBinomial2();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var negativeBinomial = factory();
	t.equal( negativeBinomial.NAME, 'negative-binomial', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the underlying PRNG', function test( t ) {
	var negativeBinomial = factory();
	t.equal( typeof negativeBinomial.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var negativeBinomial = factory({
		'seed': 12345
	});
	t.equal( typeof negativeBinomial.SEED, 'number', 'has `SEED` property' );
	t.equal( negativeBinomial.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if provided a number of failures argument which is not a positive number', function test( t ) {
	var values;
	var i;

	values = [
		-1.0,
		-0.5,
		0.0,
		NaN,
		'5',
		null,
		true,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value, 0.5 );
		};
	}
});

