'use strict';

var randn = require( './../lib' );

var seed;
var rand;
var i;

// Generate pseudorandom numbers...
console.log( '\nseed: %d', randn.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( randn() );
}

// Create a new pseudorandom number generator...
seed = 1234;
rand = randn.factory({
	'seed': seed
});
console.log( '\nseed: %d', seed );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}

// Create another pseudorandom number generator using a previous seed...
rand = randn.factory({
	'seed': randn.SEED
});
console.log( '\nseed: %d', randn.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}
