'use strict';

/**
* Rayleigh distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/rayleigh
*
* @example
* var rayleigh = require( '@stdlib/math/base/random/rayleigh' );
*
* var v = rayleigh( 2.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/rayleigh' ).factory;
* var rayleigh = factory( 3.0, {
* 	'seed': 297
* });
*
* var v = rayleigh();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/rayleigh' ).factory;
* var rayleigh = factory({
* 	'seed': 297
* });
*
* var v = rayleigh( 2.0 );
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var rayleigh = require( './rayleigh.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( rayleigh, 'factory', factory );


// EXPORTS //

module.exports = rayleigh;
