'use strict';

var randu = require( './../lib' );

var seed;
var rand;
var i;

// Generate pseudorandom numbers...
console.log( '\nseed: %d', randu.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( randu() );
}

// Create a new pseudorandom number generator...
seed = 1234;
rand = randu.factory({
	'seed': seed
});
console.log( '\nseed: %d', seed );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}

// Create another pseudorandom number generator using a previous seed...
rand = randu.factory({
	'seed': randu.SEED
});
console.log( '\nseed: %d', randu.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}
