'use strict';

// MODULES //

var isPositive = require( '@stdlib/assert/is-positive-number' ).isPrimitive;


// MAIN //

/**
* Validates parameters.
*
* @private
* @param {PositiveNumber} lambda - shape parameter
* @param {PositiveNumber} k - scale parameter
* @returns {(Error|null)} error or null
*
* @example
* var err = validate( 1.0, 2.0 );
* if ( err ) {
*     throw err;
* }
*/
function validate( lambda, k ) {
	if ( !isPositive( lambda ) ) {
		return new TypeError( 'invalid input argument. Shape parameter must be a positive number. Value: `' + lambda + '`.' );
	}
	if ( !isPositive( k ) ) {
		return new TypeError( 'invalid input argument. Scale parameter must be a positive number. Value: `' + k + '`.' );
	}
	return null;
} // end FUNCTION validate()


// EXPORTS //

module.exports = validate;
