#include "stdlib/math/base/special/acosf.h"
#include "stdlib/math/base/assert/is_nanf.h"
#include "stdlib/math/base/special/sqrtf.h"
#include "stdlib/number/float32/base/from_word.h"
#include "stdlib/number/float32/base/to_word.h"
#include "stdlib/constants/float32/pi.h"
#include <stdint.h>

static const float PIO2_HI = 1.5707962513e+00f;
static const float PIO2_LO = 7.5497894159e-08f;
static const float QS1 = -7.0662963390e-01f;

/* Begin auto-generated functions. The following functions are auto-generated. Do not edit directly. */

// BEGIN: poly_p

/**
* Evaluates a polynomial.
*
* ## Notes
*
* -   The implementation uses [Horner's rule][horners-method] for efficient computation.
*
* [horners-method]: https://en.wikipedia.org/wiki/Horner%27s_method
*
* @param x    value at which to evaluate the polynomial
* @return     evaluated polynomial
*/
static float poly_p( const float x ) {
	return 0.16666586697f + (x * (-0.042743422091f + (x * -0.008656363003f)));
}

// END: poly_p

/* End auto-generated functions. */

/**
* Computes the arccosine of a single-precision floating-point number.
*
* @param x    input value
* @return     arccosine (in radians)
*
* @example
* float out = stdlib_base_acosf( 1.0f );
* // returns 0.0f
*/
float stdlib_base_acosf( const float x ) {
	uint32_t idf;
	uint32_t hx;
	uint32_t ix;
	float df;
	float z;
	float p;
	float q;
	float r;
	float w;
	float s;
	float c;

	if ( stdlib_base_is_nanf( x ) ) {
		return 0.0f / 0.0f; // NaN
	}
	if ( x < -1.0f || x > 1.0f ) {
		return 0.0f / 0.0f; // NaN
	}
	stdlib_base_float32_to_word( x, &hx );
	ix = hx & 0x7fffffff;
	if ( ix >= 0x3f800000 ) {
		if ( ix == 0x3f800000 ) {
			if ( hx == 0x3f800000 ) {
				return 0.0f;
			}
			return STDLIB_CONSTANT_FLOAT32_PI + ( 2.0f * PIO2_LO );
		}
		return 0.0f / 0.0f; // NaN
	}
	if ( ix < 0x3f000000 ) {
		if ( ix <= 0x32800000 ) {
			return PIO2_HI + PIO2_LO;
		}
		z = x * x;
		p = z * poly_p( z );
		q = 1.0f + ( z * QS1 );
		r = p / q;
		return PIO2_HI - ( x - ( PIO2_LO - ( x * r ) ) );
	}
	z = ( 1.0f - x ) * 0.5f;
	s = stdlib_base_sqrtf( z );
	df = s;
	stdlib_base_float32_to_word( df, &idf );
	stdlib_base_float32_from_word( idf & 0xfffff000, &df );
	c = ( z - ( df * df ) ) / ( s + df );
	p = z * poly_p( z );
	q = 1.0f + ( z * QS1 );
	r = p / q;
	w = ( r * s ) + c;
	return 2.0f * ( df + w );
}
