'use strict';

/**
* Evaluate the arcsecant of a number.
*
* @module @stdlib/math/base/special/asec
*
* @example
* var asec = require( '@stdlib/math/base/special/asec' );
 *
 * @param {number} x - input value
 * @returns {number} arcsecant (in radians)
 *
 * @example
 * var v = asec( 1.0 );
 * // returns 0.0
 *
 * @example
 * var v = asec( 1.4142 ); // ~pi/4
 * // returns ~0.785
 *
 * @example
 * var v = asec( NaN );
 * // returns NaN
*/

// MODULES //

var asec = require( './asec' );


// EXPORTS //

module.exports = asec;
