'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var sqrtf = require( '@stdlib/math/base/special/sqrtf' );
var polyp = require( './poly_p.js' );


// VARIABLES //

var PIO2F = 1.5707964;


// MAIN //

/**
* Computes the arcsine of a single-precision floating-point number.
*
* ## Method
*
* -   A rational function of the form
*
*     ```tex
*     x + x^3 \frac{P(x^2)}{Q(x^2)}
*     ```
*
*     is used for \\(\|x\|\\) in the interval \\(\[0, 0.5\]\\). If \\(\|x\| > 0.5\\), it is transformed by the identity
*
*     ```tex
*     \operatorname{asinf}(x) = \frac{\pi}{2} - 2 \operatorname{asinf}( \sqrt{ (1-x)/2 } )
*     ```
*
* ## Notes
*
* -   Relative error:
*
*     | arithmetic | domain | # trials | peak    | rms     |
*     |:-----------|:-------|:---------|:--------|:--------|
*     | IEEE       | -1, 1  | 10^5     | 1.4e-7  | 4.2e-8  |
*
* @param {number} x - input value
* @returns {number} arcsine (in radians)
*
* @example
* var v = asinf( 0.0 );
* // returns ~0.0
*
* @example
* var v = asinf( 3.14/4.0 );
* // returns ~0.903
*
* @example
* var v = asinf( -3.14/6.0 );
* // returns ~-0.551
*
* @example
* var v = asinf( NaN );
* // returns NaN
*/
function asinf( x ) {
	var flag;
	var sgn;
	var xx;
	var a;
	var z;

	xx = x;
	if ( isnan( xx ) ) {
		return NaN;
	}
	if ( xx > 0.0 ) {
		sgn = 1;
		a = xx;
	}
	else {
		sgn = -1;
		a = -xx;
	}
	if ( a > 1.0 ) {
		return 0.0;
	}
	if ( a < 1.0e-4 ) {
		z = a;
		if ( sgn < 0 ) {
			z = -z;
		}
		return z;
	}
	if ( a > 0.5 ) {
		z = 0.5 * ( 1.0 - a );
		xx = sqrtf( z );
		flag = 1;
	}
	else {
		xx = a;
		z = xx * xx;
		flag = 0;
	}
	z =( polyp( z ) * z * xx ) + xx;
	if ( flag !== 0 ) {
		z += z;
		z = PIO2F - z;
	}
	if ( sgn < 0 ) {
		z = -z;
	}
	return z;
}


// EXPORTS //

module.exports = asinf;
