'use strict';

/**
* Compute the Bessel function of the first kind of order zero.
*
* @module @stdlib/math/base/special/besselj0
*
* @example
* var j0 = require( '@stdlib/math/base/special/besselj0' );
*
* var v = j0( 0.0 );
* // returns 1.0
*
* v = j0( 1.0 );
* // returns ~0.765
*
* v = j0( Infinity );
* // returns 0.0
*
* v = j0( -Infinity );
* // returns 0.0
*
* v = j0( NaN );
* // returns NaN
*/

// MODULES //

var j0 = require('./j0.js');


// EXPORTS //

module.exports = j0;
