#!/usr/bin/env julia

import JSON

"""
    gen( x, name )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> gen( x, \"data.json\" );
```
"""
function gen( x, name )
	y = Array( Float64, length( x ) );
	for i in eachindex(x)
		y[i] = bessely0( x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("expected", y)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Subnormal values:
x = linspace( 1.0e-309, 1.0e-315, 5003 );
gen( x, "subnormal.json" );

# Positive tiny values:
x = linspace( 1.0e-300, 1.0e-308, 5003 );
gen( x, "tiny_positive.json" );

# Small(er) values:
x = linspace( 1.0e-5, 0.8, 5003 );
gen( x, "smaller.json" );

# Positive small values:
x = linspace( 0.8, 3.0, 5003 );
gen( x, "small_positive.json" );

# Positive medium values:
x = linspace( 3.0, 20.0, 5003 );
gen( x, "medium_positive.json" );

# Large positive values:
x = linspace( 20.0, 50.0, 5003 );
gen( x, "large_positive.json" );

# Very large positive values:
x = linspace( 50.0, 500.0, 5003 );
gen( x, "very_large_positive.json" );

# Huge positive values:
x = linspace( 1.0e300, 1.0e308, 5003 );
gen( x, "huge_positive.json" );

# The full gamut of positive magnitudes:
x = [ 10.0 ^ x for x in linspace( -323.0, 308.0, 632 ) ];
gen( x, "positive_gamut.json" );
