tape( 'the function returns `NaN` for negative numbers', function test( t ) {
	var v;
	var x;
	var i;

	for ( i = 0; i < 1000; i++ ) {
		x = -( randu() * 100.0 ) - EPS;
		v = y0( x );
		t.equal( isnan( v ), true, 'returns NaN' );
	}
	t.end();
});

tape( 'the function returns `-Infintiy` if provided `0.0`', function test( t ) {
	var v = y0( 0.0 );
	t.equal( v, NINF, 'returns -Infinity' );
	t.end();
});

tape( 'the function returns `NaN` if provided `NaN`', function test( t ) {
	var v = y0( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

tape( 'the function returns `0.0` if provided `+infinity`', function test( t ) {
	var v = y0( PINF );
	t.equal( v, 0.0, 'returns 0.0' );
	t.end();
});

tape( 'the function returns `NaN` if provided `-infinity`', function test( t ) {
	var v = y0( NINF );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});
