'use strict';

var randu = require( '@stdlib/random/base/randu' );
var betainc = require( './../lib' );

var i;
var x;
var a;
var b;

for ( i = 0; i < 100; i++ ) {
	x = randu();
	a = randu() * 10.0;
	b = randu() * 10.0;
	console.log( 'x: %d, \t a: %d, \t b: %d, \t f(x,a,b): %d',
		x.toFixed( 4 ),
		a.toFixed( 4 ),
		b.toFixed( 4 ),
		betainc( x, a, b ).toFixed( 4 )
	);
}
