// MODULES //

var ln = require( '@stdlib/math/base/special/ln' );


// TEMME ROOT FINDER //

function temme_root_finder( t, a ) {
	return function roots( x ) {
		var big;
		var f1;
		var f;
		var y;

		y = 1 - x;
		if ( y === 0.0 ) {
			big = Number.MAX_VALUE / 4.0;
			return [ -big, -big ];
		}
		if ( x === 0.0 ) {
			big = Number.MAX_VALUE / 4.0;
			return [ -big, -big ];
		}
		f = ln( x ) + a * ln( y ) + t;
		f1 = ( 1.0 / x ) - ( a / y );
		return [ f, f1 ];
	};
} // end FUNCTION temme_root_finder()


// EXPORTS //

module.exports = temme_root_finder;
