'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var binomcoefln = require( './../lib' );

var n;
var k;
var i;

for ( i = 0; i < 100; i++ ) {
	n = round( (randu()*40.0) - 10.0 );
	k = round( randu()*20.0 );
	console.log( 'ln( %d choose %d ) = %d', n, k, binomcoefln( n, k ) );
}
