'use strict';

var randu = require( '@stdlib/random/base/randu' );
var copysign = require( './../lib' );

var x;
var y;
var z;
var i;

// Generate random double-precision floating-point numbers `x` and `y` and copy the sign of `y` to `x`...
for ( i = 0; i < 100; i++ ) {
	x = (randu()*100.0) - 50.0;
	y = (randu()*10.0) - 5.0;
	z = copysign( x, y );
	console.log( 'x: %d, y: %d => %d', x, y, z );
}
