'use strict';

// MODULES //

var cos = require( '@stdlib/math/base/special/cos' );
var deg2rad = require( '@stdlib/math/base/special/deg2rad' );
var isInteger = require( '@stdlib/math/base/assert/is-integer' );


// MAIN //

/**
* Evaluates the cosine of a degree.
*
* @param {number} x - input value (in degree)
* @returns {number} cosine
*
* @example
* var v = cosd( 0.0 );
* // returns 1.0
*
* @example
* var v = cosd( 90 );
* // returns 0.0
*
* @example
* var v = cosd( 60 );
* // returns ~0.5
*
* @example
* var v = cosd( NaN );
* // returns NaN
*/
function cosd( x ) {
	var xRad;

	if (x === Infinity || x === -Infinity) {
		return NaN;
	}

	if (isInteger(((x / 90) - 1) / 2)) {
		return 0;
	}

	xRad = deg2rad(x);

	return cos(xRad);
}


// EXPORTS //

module.exports = cosd;
