// MODULES //

var evalpoly = require( '@stdlib/math/base/tools/evalpoly' ).factory;
var cos = require( '@stdlib/math/base/special/cos' );


// VARIABLES //

var P = [
	0.041666666666666664,
	-0.0013888888888888872,
	0.00002480158730157055,
	-2.755731921499979e-7,
	2.087675428708152e-9,
	-1.147028484342536e-11,
	4.737750796424621e-14
];
var PIO4 = 7.85398163397448309616e-1; // 4/Ï€


// FUNCTIONS //

var polyval = evalpoly( P );


// MAIN //

/**
* Computes the cosine of a number minus one.
*
* @param {number} x - input value (in radians)
* @returns {number} cosine minus one
*
* @example
* var v = cosm1( 0.0 );
* // returns 0.0
*
* @example
* var v = cosm1( Math.PI/4.0 );
* // returns ~-0.291
*
* @example
* var v = cosm1( -Math.PI/6.0 );
* // returns ~~-0.134
*
* @example
* var v = cosm1( NaN );
* // returns NaN
*/
function cosm1( x ) {
	var x2;
	if ( x < -PIO4 || x > PIO4 ) {
		return cos( x ) - 1.0;
	}
	x2 = x * x;
	return ( -0.5*x2 ) + ( x2*x2*polyval( x2 ) );
} // end FUNCTION cosm1()


// EXPORTS //

module.exports = cosm1;
