'use strict';

// MODULES //

var tan = require( '@stdlib/math/base/special/tan' );


// MAIN //

/**
 * Evaluates the cotangent of a number.
 *
 * ## Method
 *
 * -   Let \\(S\\), \\(C\\), and \\(T\\) denote the \\(\sin\\), \\(\cos\\), and \\(\cot\\), respectively, on \\(\[-\pi/4, +\pi/4\]\\).
 *
 * -   We have
 *
 *     | n | sin(x) | cos(x) | cot(x) |
 *     | - | ------ | ------ | ------ |
 *     | 0 |    S   |    C   |   1/T  |
 *     | 1 |    C   |   -S   |  -T    |
 *     | 2 |   -S   |   -C   |   1/T  |
 *     | 3 |   -C   |    S   |  -T    |
 *
 *
 * @param {number} x - input value (in radians)
 * @returns {number} cotangent
 *
 * @example
 * var v = cot( 0.0 );
 * // returns Infinity
 *
 * @example
 * var v = cot( 3.141592653589793/2.0 );
 * // returns ~0.0
 *
 * @example
 * var v = cot( -3.141592653589793/4.0 );
 * // returns ~-1.0
 *
 * @example
 * var v = cot( 3.141592653589793/4.0 );
 * // returns ~1.0
 *
 * @example
 * var v = cot( NaN );
 * // returns NaN
 */
function cot(x) {
	return 1 / tan( x );
}


// EXPORTS //

module.exports = cot;
