#include "stdlib/math/base/special/coversin.h"
#include "stdlib/math/base/special/sin.h"

/**
* Computes the coversed sine.
*
* @param x    input value (in radians)
* @return     coversed sine
*
* @example
* double y = stdlib_base_coversin( 3.141592653589793 / 2.0 );
* // returns 0.0
*/
double stdlib_base_coversin( const double x ) {
	return 1.0 - stdlib_base_sin( x );
}
