'use strict';


/**
* Compute the Jacobi elliptic functions sn, cn, and dn.
*
* @module @stdlib/math/base/special/ellipj
*
* @example
* var ellipj = require( '@stdlib/math/base/special/ellipj' );
*
* var v = ellipj( 0.5, 0 );
* // returns [ 0.479, 0.878, 1 ]
*
* v = ellipj( 0.5, -1.0 );
* // returns [ 0.497, 0.868, ~1.117 ]
*
* v = ellipj( Infinity, 0.5 );
* // returns [ NaN, NaN, NaN ]
*
* v = ellipj( -Infinity );
* // returns [ NaN, NaN, NaN ]
*
* v = ellipj( NaN );
* // returns [ NaN, NaN, NaN ]
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
